/* JSONPath 0.8.0 - XPath for JSON
 *
 * Copyright (c) 2007 Stefan Goessner (goessner.net)
 * Licensed under the MIT (MIT-LICENSE.txt) licence.
 */
function jsonPath(obj, expr, arg) {
   var P = {
      resultType: arg && arg.resultType || "VALUE",
      result: [],
      normalize: function(expr) {
         var subx = [];
         return expr.replace(/[\['](\??\(.*?\))[\]']/g, function($0,$1){return "[#"+(subx.push($1)-1)+"]";})
                    .replace(/'?\.'?|\['?/g, ";")
                    .replace(/;;;|;;/g, ";..;")
                    .replace(/;$|'?\]|'$/g, "")
                    .replace(/#([0-9]+)/g, function($0,$1){return subx[$1];});
      },
      asPath: function(path) {
         var x = path.split(";"), p = "$";
         for (var i=1,n=x.length; i<n; i++)
            p += /^[0-9*]+$/.test(x[i]) ? ("["+x[i]+"]") : ("['"+x[i]+"']");
         return p;
      },
      store: function(p, v) {
         if (p) P.result[P.result.length] = P.resultType == "PATH" ? P.asPath(p) : v;
         return !!p;
      },
      trace: function(expr, val, path) {
         if (expr) {
            var x = expr.split(";"), loc = x.shift();
            x = x.join(";");
            if (val && val.hasOwnProperty(loc))
               P.trace(x, val[loc], path + ";" + loc);
            else if (loc === "*")
               P.walk(loc, x, val, path, function(m,l,x,v,p) { P.trace(m+";"+x,v,p); });
            else if (loc === "..") {
               P.trace(x, val, path);
               P.walk(loc, x, val, path, function(m,l,x,v,p) { typeof v[m] === "object" && P.trace("..;"+x,v[m],p+";"+m); });
            }
            else if (/,/.test(loc)) { 
               for (var s=loc.split(/'?,'?/),i=0,n=s.length; i<n; i++)
                  P.trace(s[i]+";"+x, val, path);
            }
            else if (/^\(.*?\)$/.test(loc))
               P.trace(P.eval(loc, val, path.substr(path.lastIndexOf(";")+1))+";"+x, val, path);
            else if (/^\?\(.*?\)$/.test(loc)) 
               P.walk(loc, x, val, path, function(m,l,x,v,p) { if (P.eval(l.replace(/^\?\((.*?)\)$/,"$1"),v[m],m)) P.trace(m+";"+x,v,p); });
            else if (/^(-?[0-9]*):(-?[0-9]*):?([0-9]*)$/.test(loc)) 
               P.slice(loc, x, val, path);
         }
         else
            P.store(path, val);
      },
      walk: function(loc, expr, val, path, f) {
         if (val instanceof Array) {
            for (var i=0,n=val.length; i<n; i++)
               if (i in val)
                  f(i,loc,expr,val,path);
         }
         else if (typeof val === "object") {
            for (var m in val)
               if (val.hasOwnProperty(m))
                  f(m,loc,expr,val,path);
         }
      },
      slice: function(loc, expr, val, path) {
         if (val instanceof Array) {
            var len=val.length, start=0, end=len, step=1;
            loc.replace(/^(-?[0-9]*):(-?[0-9]*):?(-?[0-9]*)$/g, function($0,$1,$2,$3){start=parseInt($1||start);end=parseInt($2||end);step=parseInt($3||step);});
            start = (start < 0) ? Math.max(0,start+len) : Math.min(len,start);
            end   = (end < 0)   ? Math.max(0,end+len)   : Math.min(len,end);
            for (var i=start; i<end; i+=step)
               P.trace(i+";"+expr, val, path);
         }
      },
      eval: function(x, d, _vname) 
      {
         try 
         { 
             if (arg && arg.name && typeof(arg.value) != "undefined")
             {
                 return $ && d && (d[arg.name] == arg.value); 
             }
             else
             {
                 return false;
             }
         }
         catch(e) 
         { 
             throw new SyntaxError("Please use mx.utils.JsonUtil。"); 
         }
     }
   };

   var $ = obj;
   if (expr && obj && (P.resultType == "VALUE" || P.resultType == "PATH")) {
      P.trace(P.normalize(expr).replace(/^\$;/,""), obj, "$");
      return P.result.length ? P.result : false;
   }
} 



/*********************************** JsonUtilClass **************************************************/
$ns("mx.utils");

/**
 * 提供一个通过 JsonPath 查询对象的工具类。该类在运行时唯一静态实例是 <b>mx.utils.JsonUtil</b> 对象。
 */
mx.utils.JsonUtilClass = function()
{
    var me = this;

    /**
     * 查询获取数组中相应的对象，返回对应的一个数组。
     * @param p_items 一个 Json 数组，表示在该对象中查询。
     * @param p_fieldName 一个字符串，表示要查询的字段 key。
     * @param p_value 表示要查询的值。
     * @param [p_mode=1] 一个 Number 值，表示查询 Json 数组的第几层（1 代表仅查询第一层；2 代表仅查询第二层；大于 3 代表递归查询）。
     */
    me.getItems = function(p_items, p_fieldName, p_value, p_mode)
    {
        var results = null;
        var args = {name:p_fieldName, value:p_value};
        
        if (!$.isNumeric(p_value))
        {
        	p_value = "'" + p_value + "'";
        }
        	
        if(typeof(p_mode) == "undefined" || p_mode == 1)
        {
            results =  jsonPath(p_items, '$[?(@.' + p_fieldName + '=='+ p_value + ')]', args);
        }
        else if (p_mode == 2)
        {
            results =  jsonPath(p_items, '$.*[?(@.' + p_fieldName + '=='+ p_value + ')]', args);
        }
        else
        {
            results =  jsonPath(p_items, '$..[?(@.' + p_fieldName + '=='+ p_value + ')]', args);
        }
        return results;
    };
    
    /**
     * 查询获取数组中相应的对象，返回对应的第一个对象。
     * @param p_items 一个 Json 数组，表示在该对象中查询。
     * @param p_fieldName 一个字符串，表示要查询的字段 key。
     * @param p_value 表示要查询的值。
     * @param [p_mode=1] 一个 Number 值，表示查询 Json 数组的第几层（1 代表仅查询第一层；2 代表仅查询第二层；大于 3 代表递归查询）。
     */
    me.getItem = function(p_items, p_fieldName, p_value, p_mode)
    {
        var results = me.getItems(p_items, p_fieldName, p_value, p_mode);
        if ($notEmpty(results) && results.length > 0)
        {
            return results[0];
        }
        else
        {
            return null;
        }
    };
    
    return me;
};

mx.utils.JsonUtil = new mx.utils.JsonUtilClass();